import 'dart:convert';
import 'dart:typed_data';

import 'package:flutter/material.dart';

class PluginIcons {
  static const iconSize = 32.0;

  static const defaultColor = Color(0xFFA4634D);

  static final Map<String, Uint8List> _bytesCache = {};
  static final Map<String, MemoryImage> _providerCache = {};

  static Uint8List _bytesFor(String base64) {
    return _bytesCache.putIfAbsent(base64, () => base64Decode(base64));
  }

  static MemoryImage _providerFor(String base64) {
    return _providerCache.putIfAbsent(base64, () => MemoryImage(_bytesFor(base64)));
  }

  static Widget _buildIcon(String assetPath, {Color? color}) {
    final effectiveColor = color ?? defaultColor;
    return Image(
      image: _providerFor(assetPath),
      width: iconSize,
      height: iconSize,
      color: effectiveColor,
      gaplessPlayback: true,
    );
  }

  static Widget get main => _buildIcon(
      r'');

  static Widget get clear => _buildIcon(
      r'');

  static Widget get alignRuler => _buildIcon(
      r'');

  static Widget get appDeviceInfo => _buildIcon(
      r'');

  static Widget get appLog => _buildIcon(
      r'');

  static Widget get colorPicker => _buildIcon(
      r'');

  static Widget get database => _buildIcon(
      r'');

  static Widget get dio => _buildIcon(
      r'');

  static Widget get fileManager => _buildIcon(
      r'');

  static Widget get frameRate => _buildIcon(
      r'');

  static Widget get performance => _buildIcon(
      r'');

  static Widget get proxy => _buildIcon(
      r'');

  static Widget get regular => _buildIcon(
      r'');

  static Widget get routeHistory => _buildIcon(
      r'');

  static Widget get widgetDetail => _buildIcon(
      r'');

  static Widget get widgetInfo => _buildIcon(
      r'');
}
